mapper.select:
SELECT tbl_order.id, tbl_order.order_no, tbl_order.order_status, tbl_order.order_status_vice, tbl_order.goods_count, tbl_order.add_time, tbl_order.order_type, tbl_order.order_channel, tbl_order.note, tbl_order.order_amount_total, tbl_order.member_shop_id memberId FROM tbl_order
tbl_order.order_status = #{status}
and tbl_order.order_status_vice = #{statusVice}
AND tbl_order.shop_id = #{shopId}
ORDER BY tbl_order.add_time
ORDER BY tbl_order.add_time DESC
ORDER BY tbl_order.order_amount_total
ORDER BY tbl_order.add_time DESC
LIMIT #{pageStart},#{pageEnd}

SELECT tbl_outbound_order.outbound_order_no, tbl_outbound_order_goods.amount, tbl_outbound_order_goods.shop_id, tbl_outbound_order_goods.warehouse_id, tbl_order_goods.goods_sku_id, tbl_outbound_order_goods.order_goods_id FROM tbl_outbound_order INNER JOIN tbl_outbound_order_goods ON tbl_outbound_order_goods.outbound_order_id = tbl_outbound_order.id INNER JOIN tbl_order_goods ON tbl_outbound_order_goods.order_goods_id = tbl_order_goods.id
tbl_outbound_order.outbound_order_no = #{outboundNo}
tbl_outbound_order.order_no = #{orderNo}

SELECT tbl_user.head_portrait, tbl_member_shop.member_name, tbl_outbound_order.outbound_order_status, tbl_outbound_order.add_time, tbl_outbound_order.goods_count, tbl_outbound_order.outbound_order_no FROM tbl_outbound_order INNER JOIN tbl_order ON tbl_outbound_order.order_no = tbl_order.order_no INNER JOIN tbl_member_shop ON tbl_order.member_shop_id = tbl_member_shop.id INNER JOIN tbl_user ON tbl_member_shop.user_id = tbl_user.id
tbl_outbound_order.outbound_order_status = #{status}
AND CONCAT( tbl_member_shop.member_name, tbl_member_shop.phone, tbl_order.order_no ) LIKE CONCAT("%",#{keyword},"%")
LIMIT #{pageStart},#{pageEnd}
